import {useState} from "react";
import 'bulma/css/bulma.min.css';

function generateId(){
    return Math.floor((1 + Math.random()) * 0x10000)
        .toString(16)
        .substring(1)
}

function App() {
    const [title, setTitle] = useState("")
    const [todos, setTodos] = useState([])

    function onTodoAdd(){
        setTodos([...todos,{id:generateId(), title, editMode:false}])
        setTitle("")
    }

    function onTodoDelete(id){
        setTodos(todos.filter(todo => todo.id !== id))
    }

    function onTodoEdit(id){
        setTodos(todos.map(todo => todo.id === id ? {...todo, editMode:true} : {...todo}))
    }

    function onTodoEditDone(id,title){
        setTodos(todos.map(todo => todo.id === id ? {...todo, editMode:false, title:title} : {...todo}))
    }

  return (
    <div>

        <h1> Test</h1>
    <div class="field has-addons">
          <div class="control">
             <input class="input is-focused value"  value={title} onChange={(e)=> setTitle(e.target.value)}/>
          </div>
          <div class="control">
          <button class="button is-primary" onClick={onTodoAdd}>Add </button>
    </div>
    </div>     



              {todos.map(todo => <Todo
                  todo={todo}
                  onTodoDelete={()=> onTodoDelete(todo.id)}
                  onTodoEdit={()=> onTodoEdit(todo.id)}
                  onEditDone={(id,title)=> onTodoEditDone(id,title)}
              />)}

          </div> 

  )
}

function Todo({todo, onTodoDelete, onTodoEdit, onEditDone}){
    const [newTitle, setNewTitle] = useState(todo.title)
    return (
        <div class="field has-addons">

        <div className="card mb-4">
            {!todo.editMode ?
                <header className="card-header">
                    <div className="card-header-title is-expanded">
                        <div className="control">
                            <span>{todo.title}</span>
                        </div>
                    </div>
                    <button class="button is-danger is-light" onClick={onTodoDelete}>Delete</button>
                    <button class="button is-info is-light" onClick={onTodoEdit}>Edit</button>
                </header>
                :
                <div>
                    <input class="input is-focused value" value={newTitle} onChange={e => setNewTitle(e.target.value)}/>
                    <button class="button is-info is-light" onClick={()=> onEditDone(todo.id, newTitle)}>Edit Done</button>
                </div>

            }
        </div>

     </div>

    )
}
export default App
