import {useState} from "react";
import "https://cdn.jsdelivr.net/npm/bulma@0.9.1/css/bulma.min.css"

function generateId(){
    return Math.floor((1 + Math.random()) * 0x10000)
        .toString(16)
        .substring(1)
}

function App() {
    const [title, setTitle] = useState("")
    const [todos, setTodos] = useState([])

    function onTodoAdd(){
        setTodos([...todos,{id:generateId(), title, editMode:false}])
        setTitle("")
    }

    function onTodoDelete(id){
        setTodos(todos.filter(todo => todo.id !== id))
    }

    function onTodoEdit(id){
        setTodos(todos.map(todo => todo.id === id ? {...todo, editMode:true} : {...todo}))
    }

    function onTodoEditDone(id,title){
        setTodos(todos.map(todo => todo.id === id ? {...todo, editMode:false, title:title} : {...todo}))
    }

  return (
        <div>
            <input value={title} onChange={(e)=> setTitle(e.target.value)}/>
            <button onClick={onTodoAdd}>+</button>
            {todos.map(todo => <Todo
                todo={todo}
                onTodoDelete={()=> onTodoDelete(todo.id)}
                onTodoEdit={()=> onTodoEdit(todo.id)}
                onEditDone={(id,title)=> onTodoEditDone(id,title)}
            />)}
        </div>
  )
}

function Todo({todo, onTodoDelete, onTodoEdit, onEditDone}){
    const [newTitle, setNewTitle] = useState(todo.title)
    return (
        <div className="card mb-4">
            {!todo.editMode ?
                <header className="card-header">
                    <div className="card-header-title is-expanded">
                        <div className="control">
                            <span>{todo.title}</span>
                        </div>
                    </div>
                    <button onClick={onTodoDelete}>Delete</button>
                    <button onClick={onTodoEdit}>Edit</button>
                </header>
                :
                <div>
                    <input value={newTitle} onChange={e => setNewTitle(e.target.value)}/>
                    <button onClick={()=> onEditDone(todo.id, newTitle)}>Edit Done</button>
                </div>

            }
        </div>
    )
}
export default App
